mapper.select:
select count(*) from recipe

	where rname like#{keyword}


	where mcategory = #{mcategory}

order by rnum desc

select * from recipe

	where rname like#{keyword}


	where mcategory = #{mcategory}

order by rnum desc

select count(*) from food

	where fname like#{keyword}

order by fnum desc

select * from food

	where fname like#{keyword}

order by fnum desc
